import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './PurchasedCourses.css'; // Import the CSS file

const PurchasedCourses = () => {
  const [purchasedCourses, setPurchasedCourses] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPurchasedCourses = async () => {
      try {
        const res = await axios.get('http://127.0.0.1:8000/api/purchased-courses/', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        
        // Map through the courses and set the absolute URL for images
        const coursesWithAbsoluteUrls = res.data.map(course => ({
          ...course,
          image_url: course.image_url ? `http://127.0.0.1:8000${course.image_url}` : 'path/to/default-image.jpg', // Prepend the base URL or use a default image
        }));
        
        setPurchasedCourses(coursesWithAbsoluteUrls);
      } catch (err) {
        console.error(err);
      }
    };

    fetchPurchasedCourses();
  }, []);

  const handleOpenCourse = (courseId) => {
    navigate(`/course/${courseId}`);  // Navigate to course page
  };

  return (
    <div className="purchased-courses">
      <h2>Purchased Courses</h2>
      {purchasedCourses.length === 0 ? (
        <div className="no-courses">You have not purchased any courses yet.</div>
      ) : (
        <div className="course-list">
          {purchasedCourses.map(course => (
            <div className="course-card" key={course.id}>
              <img src={course.image_url} alt={course.title} className="course-image" onError={(e) => e.target.src = 'path/to/default-image.jpg'} /> {/* Default image on error */}
              <div className="course-info">
                <h3>{course.title}</h3>
                <button className="open-button" onClick={() => handleOpenCourse(course.id)}>Open Course</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PurchasedCourses;
