// HomePage.js
import React from 'react';
import CourseCard from './CourseCard';
import './Home.css'; // Ensure the CSS file is linked correctly

const HomePage = () => {
  const courses = [
    { id: 1, title: 'React for Beginners', description: 'Learn React from scratch!', image: 'course1.jpg' },
    { id: 2, title: 'Advanced Python', description: 'Master advanced Python topics.', image: 'course2.jpg' },
    { id: 3, title: 'HTML & CSS Basics', description: 'Begin your journey into web development.', image: 'course3.jpg' },
  ];

  return (
    <div className="homepage">
      <h1>Welcome to Our E-Learning Platform</h1>
      <div className="course-list">
        {courses.map(course => (
          <CourseCard
            key={course.id}
            image={course.image}
            title={course.title}
            description={course.description}
          />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
