import React, { useState } from 'react';
import axios from 'axios';
import Notification from './Notification';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [notificationVisible, setNotificationVisible] = useState(false);
  const navigate = useNavigate(); // Initialize useNavigate

  const handleLogin = (e) => {
    e.preventDefault();

    axios.post('http://127.0.0.1:8000/api/token/', { username, password })
      .then(response => {
        localStorage.setItem('token', response.data.access);
        checkUserProfile(); // Check if user has a profile after login
      })
      .catch(error => {
        console.error('Login error:', error);
      });
  };

  const checkUserProfile = () => {
    axios.get('http://127.0.0.1:8000/api/user/profile/', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(res => {
      if (res.data.create_profile) {
        // Redirect to profile creation if no profile exists
        navigate('/user/profile');
      } else {
        // Redirect to homepage if the user has a profile
        navigate('/');
      }
    })
    .catch(err => {
      console.error(err);
    });
  };

  const closeNotification = () => {
    setNotificationVisible(false);
  };

  return (
    <div>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          value={username}
          onChange={e => setUsername(e.target.value)}
          placeholder="Username"
          required
        />
        <input
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <button type="submit">Login</button>
      </form>

      {notificationVisible && (
        <Notification
          message="You don't have a profile yet. Please create one."
          onClose={closeNotification}
        />
      )}
    </div>
  );
};

export default Login;
