import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserProfile = () => {
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    fullname: '',
    age: '',
    skill_level: '',
    email: '',
    profile_picture: null,
    phone_number: '',
  });

  useEffect(() => {
    // Fetch user profile
    axios.get('http://127.0.0.1:8000/api/user/profile/', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(res => {
      setProfile(res.data);
      setFormData(res.data); // Set initial form data to fetched profile data
    })
    .catch(err => console.error(err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, profile_picture: e.target.files[0] });
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    Object.keys(formData).forEach(key => {
      formDataToSend.append(key, formData[key]);
    });

    axios.post('http://127.0.0.1:8000/api/user/profile/update/', formDataToSend, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(res => {
      setProfile(res.data);
      setIsEditing(false);
    })
    .catch(err => console.error(err));
  };

  if (!profile) return <div>Loading...</div>;

  return (
    <div>
      <h2>User Profile</h2>
      {isEditing ? (
        <form onSubmit={handleUpdate}>
          <input type="text" name="fullname" value={formData.fullname} onChange={handleChange} placeholder="Full Name" required />
          <input type="number" name="age" value={formData.age} onChange={handleChange} placeholder="Age" required />
          <select name="skill_level" value={formData.skill_level} onChange={handleChange}>
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>
          <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" required />
          <input type="file" name="profile_picture" onChange={handleFileChange} />
          <input type="text" name="phone_number" value={formData.phone_number} onChange={handleChange} placeholder="Phone Number" />
          <button type="submit">Update</button>
        </form>
      ) : (
        <div>
          <p><strong>Full Name:</strong> {profile.fullname}</p>
          <p><strong>Age:</strong> {profile.age}</p>
          <p><strong>Skill Level:</strong> {profile.skill_level}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          <p><strong>Phone Number:</strong> {profile.phone_number}</p>
          {profile.profile_picture && <img src={`http://127.0.0.1:8000${profile.profile_picture}`} alt="Profile" />}
          <button onClick={() => setIsEditing(true)}>Edit Profile</button>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
