import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserProfile.css'; // Make sure this file exists

const UserProfile = () => {
  const [profile, setProfile] = useState(null);
  const [creatingProfile, setCreatingProfile] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const res = await axios.get('http://127.0.0.1:8000/api/user/profile/', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });

        console.log('Profile data:', res.data); // Log the profile data

        if (res.data.create_profile) {
          setCreatingProfile(true);
        } else {
          const profileData = res.data;
          if (profileData.profile_picture) {
            profileData.profile_picture = `http://127.0.0.1:8000${profileData.profile_picture}`; // Ensure absolute URL
          }
          console.log('Profile picture URL:', profileData.profile_picture); // Log the image URL
          setProfile(profileData);
        }
      } catch (error) {
        console.error('Failed to fetch user profile:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile(); // Fetch user profile
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Handle profile update form submission...
  };

  if (loading) {
    return <div>Loading...</div>; // Loading message
  }

  return (
    <div className="profile-container">
      {creatingProfile ? (
        <form onSubmit={handleSubmit}>
          {/* Form fields for creating/updating profile */}
        </form>
      ) : (
        profile ? (
          <div className="profile-info">
            <h2>User Profile</h2>
            <p><strong>Full Name:</strong> {profile.fullname}</p>
            <p><strong>Age:</strong> {profile.age}</p>
            <p><strong>Skill Level:</strong> {profile.skill_level}</p>
            <p><strong>Email:</strong> {profile.email}</p>
            <p><strong>Phone Number:</strong> {profile.phone_number}</p>
            {profile.profile_picture ? (
              <img 
                src={profile.profile_picture} 
                alt="Profile" 
                className="profile-image"
                onError={(e) => {
                  e.target.onerror = null; 
                  e.target.src = 'http://example.com/path/to/default/image.jpg'; // Fallback image
                }} 
              />
            ) : (
              <p>No profile picture available</p>
            )}
            <button onClick={() => setCreatingProfile(true)}>Edit Profile</button>
          </div>
        ) : (
          <div className="no-profile">
            <h2>No Profile Found</h2>
            <button onClick={() => setCreatingProfile(true)}>Create Profile</button>
          </div>
        )
      )}
    </div>
  );
};

export default UserProfile;
