import React from 'react';
import { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import CourseList from './CourseList';
import CoursePage from './CoursePage';
import PurchasedCourses from './PurchasedCourses';
import Login from './Login';
import Signup from './Signup';
import Navbar from './Navbar'; 
import Logout from './Logout'; // Import the Logout component
import Course1 from './Course1';
import Course2 from './Course2';
import UserProfile from './UserProfile';
import Notification from './Notification';


const App = () => {
  const [notificationVisible, setNotificationVisible] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');

  const showNotification = (message) => {
    setNotificationMessage(message);
    setNotificationVisible(true);
  };

  const closeNotification = () => {
    setNotificationVisible(false);
  };

  return (
    <Router>
      <div>
        {notificationVisible && (
          <Notification message={notificationMessage} onClose={closeNotification} />
        )}
        <Route path="/login">
          <Login showNotification={showNotification} />
        </Route>
        <Route path="/user/profile" component={UserProfile} />
        <Route path="/user/profile" component={UserProfile} /> 
        <Route path="/" element={<CourseList />} /> 
        <Route path="/course/:id" element={<CoursePage />} /> 
        <Route path="/user/profile" element={<UserProfile />} /> 
        <Route path="/purchased-courses" element={<PurchasedCourses />} /> 
        <Route path="/signup" element={<Signup />} /> 
        <Route path="/logout" element={<Logout />} /> 
        <Route path="/course1" element={<Course1 />} /> <Route path="/course2" element={<Course2 />} />
      </div>
    </Router>
  );
};

export default App;
