import React from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const token = localStorage.getItem('token'); // Check if the user is logged in

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token from local storage
    window.location.reload(); // Reload the page or redirect to home
  };

  return (
    <nav>
      <Link to="/">Home</Link>
      <Link to="/purchased-courses">My Courses</Link>
      {token && <Link to="/user/profile">Profile</Link>} {/* Profile link */}

      {!token ? (
        <>
          <Link to="/login">Login</Link>
          <Link to="/signup">Signup</Link>
        </>
      ) : (
        <Link to="#" onClick={handleLogout}>Logout</Link> // Logout link
      )}
    </nav>
  );
};

export default Navbar;
