import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CourseList.css'; // Import your custom CSS file
import sqlImage from './images/sql.jpg'; // Corrected import
import webDevImage from './images/Web Development.jpg'; // Corrected import
import securityPolicyImage from './images/Politique de sécurité.jpg'; // Corrected import
import Modal from './Modal'; // Import the Modal component

const CourseList = () => {
  const [courses, setCourses] = useState([]);
  const [purchasedCourses, setPurchasedCourses] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  const [currentPage, setCurrentPage] = useState(1);
  const [coursesPerPage] = useState(6);
  const [isModalOpen, setModalOpen] = useState(false); // Modal visibility
  const [selectedCourse, setSelectedCourse] = useState(null); // Selected course for purchase

  const categories = [
    { value: '', label: 'All Categories' },
    { value: 'web', label: 'Web Development' },
    { value: 'data', label: 'Data Science' },
    { value: 'cyber', label: 'Cyber Security' },
  ];

  useEffect(() => {
    fetchCourses();
    fetchPurchasedCourses();
  }, [selectedCategory, sortOrder]);

  const fetchCourses = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`http://127.0.0.1:8000/api/courses/?category=${selectedCategory}`);
      setCourses(res.data);
    } catch (err) {
      setError('Failed to fetch courses');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const fetchPurchasedCourses = async () => {
    try {
      const res = await axios.get('http://127.0.0.1:8000/api/purchased-courses/', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setPurchasedCourses(res.data.map(course => course.id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleBuyCourse = (courseId) => {
    setSelectedCourse(courses.find(course => course.id === courseId)); // Find the selected course
    setModalOpen(true); // Open the modal
  };

  const confirmPurchase = async () => {
    if (selectedCourse) {
      try {
        await axios.post(`http://127.0.0.1:8000/api/buy-course/${selectedCourse.id}/`, {}, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        alert('Course purchased successfully');
        fetchPurchasedCourses();
      } catch (err) {
        console.error(err);
      } finally {
        setModalOpen(false); // Close the modal after purchase
      }
    }
  };

  const mapCourseImages = (courses) => {
    // Create a mapping of course titles to image URLs
    const imageMapping = {
      'SQL': sqlImage,
      'Web Development': webDevImage,
      'Politique de sécurité': securityPolicyImage,
    };
  
    return courses.map(course => {
      const imageUrl = imageMapping[course.title] || ''; // Default to an empty string if not found
      return { ...course, imageUrl }; // Return a new object with the image URL added
    });
  };
  
  const filteredCourses = mapCourseImages(courses)
    .filter(course => course.title.toLowerCase().includes(searchQuery.toLowerCase()))
    .sort((a, b) => sortOrder === 'asc' ? a.price - b.price : b.price - a.price);

  // Pagination Logic
  const indexOfLastCourse = currentPage * coursesPerPage;
  const indexOfFirstCourse = indexOfLastCourse - coursesPerPage;
  const currentCourses = filteredCourses.slice(indexOfFirstCourse, indexOfLastCourse);
  const totalPages = Math.ceil(filteredCourses.length / coursesPerPage);

  return (
    <div className="course-list">
      <h2>All Courses</h2>

      <div className="filters">
        <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          {categories.map(category => (
            <option key={category.value} value={category.value}>
              {category.label}
            </option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Search courses..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />

        <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
          <option value="asc">Sort by Price (Low to High)</option>
          <option value="desc">Sort by Price (High to Low)</option>
        </select>
      </div>

      {loading ? (
        <div className="loading">Loading courses...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <div className="course-grid">
          {currentCourses.map(course => (
            <div className="course-card" key={course.id}>
              <img src={course.imageUrl} alt={course.title} className="course-image" />
              <div className="course-info">
                <h3>{course.title}</h3>
                <p>{course.description}</p>
                <p className="course-price">Price: <span>${course.price}</span></p>
                <p className="course-rating">Rating: {course.rating} ⭐</p>
                {!purchasedCourses.includes(course.id) ? (
                  <button className="buy-button" onClick={() => handleBuyCourse(course.id)}>Buy Now</button>
                ) : (
                  <p className="purchased-message">Course Purchased</p>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      <div className="pagination">
        {Array.from({ length: totalPages }, (_, index) => (
          <button
            key={index}
            onClick={() => setCurrentPage(index + 1)}
            className={currentPage === index + 1 ? 'active' : ''}
          >
            {index + 1}
          </button>
        ))}
      </div>

      {/* Modal Component */}
      <Modal 
        isOpen={isModalOpen} 
        onClose={() => setModalOpen(false)} 
        course={selectedCourse} 
        confirmPurchase={confirmPurchase} 
      />
    </div>
  );
};

export default CourseList;
