import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserProfile.css'; // Import the CSS file

const UserProfile = () => {
  const [profile, setProfile] = useState(null);
  const [creatingProfile, setCreatingProfile] = useState(false);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    fullname: '',
    age: '',
    skill_level: '',
    email: '',
    profile_picture: null,
    phone_number: '',
  });

  useEffect(() => {
    // Fetch user profile
    axios.get('http://127.0.0.1:8000/api/user/profile/', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(res => {
      if (res.data.create_profile) {
        setCreatingProfile(true); // Flag to show the profile creation form
      } else {
        const profileData = res.data;
        // Prepend base URL to profile picture if necessary
        if (profileData.profile_picture) {
          profileData.profile_picture = `http://127.0.0.1:8000${profileData.profile_picture}`;
        }
        setProfile(profileData); // Set existing profile data
      }
    })
    .catch(err => console.error(err))
    .finally(() => {
      setLoading(false); // Set loading to false after data fetch
    });
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value, // Handle file input
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const form = new FormData();
    Object.keys(formData).forEach(key => {
      form.append(key, formData[key]);
    });

    axios.post('http://127.0.0.1:8000/api/user/profile/update/', form, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}`, 'Content-Type': 'multipart/form-data' }
    })
    .then(res => {
      const updatedProfile = res.data;
      if (updatedProfile.profile_picture) {
        updatedProfile.profile_picture = `http://127.0.0.1:8000${updatedProfile.profile_picture}`;
      }
      setProfile(updatedProfile); // Set updated profile
      setCreatingProfile(false); // Hide the creation form
    })
    .catch(err => console.error(err));
  };

  if (loading) {
    return <div>Loading...</div>; // Display loading message
  }

  return (
    <div className="profile-container">
      {creatingProfile ? (
        <form onSubmit={handleSubmit}>
          <h2>Create Profile</h2>
          <input 
            type="text" 
            name="fullname" 
            placeholder="Full Name" 
            onChange={handleChange} 
            required 
          />
          <input 
            type="number" 
            name="age" 
            placeholder="Age" 
            onChange={handleChange} 
            required 
          />
          <select name="skill_level" onChange={handleChange} required>
            <option value="">Select Skill Level</option>
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>
          <input 
            type="email" 
            name="email" 
            placeholder="Email" 
            onChange={handleChange} 
            required 
          />
          <input 
            type="file" 
            name="profile_picture" 
            onChange={handleChange} 
          />
          <input 
            type="text" 
            name="phone_number" 
            placeholder="Phone Number" 
            onChange={handleChange} 
            required 
          />
          <button type="submit">Submit</button>
        </form>
      ) : (
        profile ? (
          <div className="profile-info">
            <h2>User Profile</h2>
            <img src={profile.profile_picture} alt="Profile" />
            <p><strong>Full Name:</strong> {profile.fullname}</p>
            <p><strong>Age:</strong> {profile.age}</p>
            <p><strong>Skill Level:</strong> {profile.skill_level}</p>
            <p><strong>Email:</strong> {profile.email}</p>
            <p><strong>Phone Number:</strong> {profile.phone_number}</p>
            
            <button onClick={() => setCreatingProfile(true)}>Edit Profile</button>
          </div>
        ) : (
          <div className="no-profile">
            <h2>No Profile Found</h2>
            <p>You do not have a profile yet. Please create one.</p>
            <button onClick={() => setCreatingProfile(true)}>Create Profile</button>
          </div>
        )
      )}
    </div>
  );
};

export default UserProfile;
