import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserProfile = () => {
  const [profile, setProfile] = useState(null);
  const [creatingProfile, setCreatingProfile] = useState(false);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    fullname: '',
    age: '',
    skill_level: '',
    email: '',
    profile_picture: null,
    phone_number: '',
  });
  const [newProfilePicture, setNewProfilePicture] = useState(null); // State for new profile picture

  useEffect(() => {
    // Fetch user profile
    axios.get('http://127.0.0.1:8000/api/user/profile/', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(res => {
      if (res.data.create_profile) {
        setCreatingProfile(true); // Flag to show the profile creation form
      } else {
        setProfile(res.data); // Set existing profile data
      }
    })
    .catch(err => console.error(err))
    .finally(() => {
      setLoading(false); // Set loading to false after data fetch
    });
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value, // Handle file input
    });
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    const form = new FormData();
    Object.keys(formData).forEach(key => {
      form.append(key, formData[key]);
    });

    axios.post('http://127.0.0.1:8000/api/user/profile/update/', form, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}`, 'Content-Type': 'multipart/form-data' }
    })
    .then(res => {
      setProfile(res.data); // Set updated profile
      setCreatingProfile(false); // Hide the creation form
    })
    .catch(err => console.error(err));
  };

  const handlePictureChange = (e) => {
    setNewProfilePicture(e.target.files[0]); // Set the new profile picture
  };

  const handlePictureSubmit = (e) => {
    e.preventDefault();
    const form = new FormData();
    form.append('profile_picture', newProfilePicture); // Append the new profile picture

    axios.post('http://127.0.0.1:8000/api/user/profile/update/', form, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}`, 'Content-Type': 'multipart/form-data' }
    })
    .then(res => {
      setProfile(res.data); // Update the profile with the new picture
      setNewProfilePicture(null); // Reset the new picture state
    })
    .catch(err => console.error(err));
  };

  if (loading) {
    return <div>Loading...</div>; // Display loading message
  }

  return (
    <div>
      {creatingProfile ? (
        <form onSubmit={handleProfileSubmit}>
          <h2>Create Profile</h2>
          <input 
            type="text" 
            name="fullname" 
            placeholder="Full Name" 
            onChange={handleChange} 
            required 
          />
          <input 
            type="number" 
            name="age" 
            placeholder="Age" 
            onChange={handleChange} 
            required 
          />
          <select name="skill_level" onChange={handleChange} required>
            <option value="">Select Skill Level</option>
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>
          <input 
            type="email" 
            name="email" 
            placeholder="Email" 
            onChange={handleChange} 
            required 
          />
          <input 
            type="text" 
            name="phone_number" 
            placeholder="Phone Number" 
            onChange={handleChange} 
            required 
          />
          <button type="submit">Submit</button>
        </form>
      ) : (
        profile ? (
          <div>
            <h2>User Profile</h2>
            <p><strong>Full Name:</strong> {profile.fullname}</p>
            <p><strong>Age:</strong> {profile.age}</p>
            <p><strong>Skill Level:</strong> {profile.skill_level}</p>
            <p><strong>Email:</strong> {profile.email}</p>
            <p><strong>Phone Number:</strong> {profile.phone_number}</p>
            {profile.profile_picture && <img src={profile.profile_picture} alt="Profile" />}
            <button onClick={() => setCreatingProfile(true)}>Edit Profile</button>

            {/* Change Profile Picture Section */}
            <div>
              <h3>Change Profile Picture</h3>
              <form onSubmit={handlePictureSubmit}>
                <input type="file" onChange={handlePictureChange} />
                <button type="submit">Change Picture</button>
              </form>
            </div>
          </div>
        ) : (
          <div>
            <h2>No Profile Found</h2>
            <p>You do not have a profile yet. Please create one.</p>
            <button onClick={() => setCreatingProfile(true)}>Create Profile</button>
          </div>
        )
      )}
    </div>
  );
};

export default UserProfile;
