import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CourseList.css'; // Import the CSS file for styling

const CourseList = () => {
  const [courses, setCourses] = useState([]);
  const [purchasedCourses, setPurchasedCourses] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const categories = [
    { value: '', label: 'All Categories' },
    { value: 'web', label: 'Web Development' },
    { value: 'data', label: 'Data Science' },
    { value: 'cyber', label: 'Cyber Security' },
  ];

  useEffect(() => {
    fetchCourses();
    fetchPurchasedCourses();
  }, [selectedCategory]);

  const fetchCourses = () => {
    axios.get(`http://127.0.0.1:8000/api/courses/?category=${selectedCategory}`)
      .then(res => setCourses(res.data))
      .catch(err => console.error('Error fetching courses:', err));
  };

  const fetchPurchasedCourses = () => {
    axios.get('http://127.0.0.1:8000/api/purchased-courses/', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(res => setPurchasedCourses(res.data.map(course => course.id)))
    .catch(err => console.error('Error fetching purchased courses:', err));
  };

  const handleBuyCourse = (courseId) => {
    axios.post(`http://127.0.0.1:8000/api/buy-course/${courseId}/`, {}, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(() => {
      alert('Course purchased successfully');
      fetchPurchasedCourses(); // Refresh purchased courses
    })
    .catch(err => console.error('Error purchasing course:', err));
  };

  return (
    <div className="course-list">
      <h2>All Courses</h2>

      {/* Dropdown for category selection */}
      <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)} className="category-dropdown">
        {categories.map(category => (
          <option key={category.value} value={category.value}>
            {category.label}
          </option>
        ))}
      </select>

      <div className="courses-grid">
        {courses.map(course => (
          <div key={course.id} className="course-card">
            <img src={course.image || '/default-course-image.jpg'} alt={course.title} className="course-image" />
            <h3 className="course-title">{course.title}</h3>
            <p className="course-description">{course.description}</p>
            <p className="course-price">${course.price}</p>
            {!purchasedCourses.includes(course.id) ? (
              <button onClick={() => handleBuyCourse(course.id)} className="buy-button">Buy Now</button>
            ) : (
              <button disabled className="bought-button">Purchased</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CourseList;
