import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CourseList.css'; // Import your custom CSS file

const CourseList = () => {
  const [courses, setCourses] = useState([]);
  const [purchasedCourses, setPurchasedCourses] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc'); // Sort by price (asc or desc)
  const [currentPage, setCurrentPage] = useState(1);
  const [coursesPerPage] = useState(6); // Number of courses per page

  const categories = [
    { value: '', label: 'All Categories' },
    { value: 'web', label: 'Web Development' },
    { value: 'data', label: 'Data Science' },
    { value: 'cyber', label: 'Cyber Security' },
  ];

  useEffect(() => {
    fetchCourses();
    fetchPurchasedCourses();
  }, [selectedCategory, sortOrder]);

  const fetchCourses = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`http://127.0.0.1:8000/api/courses/?category=${selectedCategory}`);
      setCourses(res.data);
    } catch (err) {
      setError('Failed to fetch courses');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const fetchPurchasedCourses = async () => {
    try {
      const res = await axios.get('http://127.0.0.1:8000/api/purchased-courses/', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setPurchasedCourses(res.data.map(course => course.id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleBuyCourse = async (courseId) => {
    try {
      await axios.post(`http://127.0.0.1:8000/api/buy-course/${courseId}/`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      alert('Course purchased successfully');
      fetchPurchasedCourses(); // Refresh purchased courses
    } catch (err) {
      console.error(err);
    }
  };

  const filteredCourses = courses
    .filter(course => course.title.toLowerCase().includes(searchQuery.toLowerCase()))
    .sort((a, b) => sortOrder === 'asc' ? a.price - b.price : b.price - a.price);

  // Pagination Logic
  const indexOfLastCourse = currentPage * coursesPerPage;
  const indexOfFirstCourse = indexOfLastCourse - coursesPerPage;
  const currentCourses = filteredCourses.slice(indexOfFirstCourse, indexOfLastCourse);
  const totalPages = Math.ceil(filteredCourses.length / coursesPerPage);

  return (
    <div className="course-list">
      <h2>All Courses</h2>

      <div className="filters">
        <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          {categories.map(category => (
            <option key={category.value} value={category.value}>
              {category.label}
            </option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Search courses..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />

        <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
          <option value="asc">Sort by Price (Low to High)</option>
          <option value="desc">Sort by Price (High to Low)</option>
        </select>
      </div>

      {loading ? (
        <p>Loading courses...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <div className="course-grid">
          {currentCourses.map(course => (
            <div className="course-card" key={course.id}>
              <img src={course.imageUrl} alt={course.title} />
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <p>Price: ${course.price}</p>
              <p>Rating: {course.rating} ⭐</p>
              {!purchasedCourses.includes(course.id) && (
                <button onClick={() => handleBuyCourse(course.id)}>Buy Now</button>
              )}
            </div>
          ))}
        </div>
      )}

      <div className="pagination">
        {Array.from({ length: totalPages }, (_, index) => (
          <button
            key={index}
            onClick={() => setCurrentPage(index + 1)}
            className={currentPage === index + 1 ? 'active' : ''}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default CourseList;
