import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserProfile = () => {
  const [profile, setProfile] = useState(null);
  const [creatingProfile, setCreatingProfile] = useState(false);
  const [formData, setFormData] = useState({
    fullname: '',
    age: '',
    skill_level: '',
    email: '',
    profile_picture: null,
    phone_number: '',
  });

  useEffect(() => {
    // Fetch user profile
    axios.get('http://127.0.0.1:8000/api/user-profile/', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(res => {
      if (res.data.create_profile) {
        setCreatingProfile(true); // Flag to show the profile creation form
      } else {
        setProfile(res.data);
      }
    })
    .catch(err => console.error(err));
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value, // Handle file input
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const form = new FormData();
    Object.keys(formData).forEach(key => {
      form.append(key, formData[key]);
    });

    axios.post('http://127.0.0.1:8000/api/update-user-profile/', form, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}`, 'Content-Type': 'multipart/form-data' }
    })
    .then(res => {
      setProfile(res.data); // Set updated profile
      setCreatingProfile(false); // Hide the creation form
    })
    .catch(err => console.error(err));
  };

  return (
    <div>
      {creatingProfile ? (
        <form onSubmit={handleSubmit}>
          <h2>Create Profile</h2>
          <input type="text" name="fullname" placeholder="Full Name" onChange={handleChange} required />
          <input type="number" name="age" placeholder="Age" onChange={handleChange} required />
          <select name="skill_level" onChange={handleChange} required>
            <option value="">Select Skill Level</option>
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>
          <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
          <input type="file" name="profile_picture" onChange={handleChange} />
          <input type="text" name="phone_number" placeholder="Phone Number" onChange={handleChange} required />
          <button type="submit">Submit</button>
        </form>
      ) : (
        profile && (
          <div>
            <h2>User Profile</h2>
            <p>Full Name: {profile.fullname}</p>
            <p>Age: {profile.age}</p>
            <p>Skill Level: {profile.skill_level}</p>
            <p>Email: {profile.email}</p>
            <p>Phone Number: {profile.phone_number}</p>
            {profile.profile_picture && <img src={profile.profile_picture} alt="Profile" />}
          </div>
        )
      )}
    </div>
  );
};

export default UserProfile;
